USE `lentokone`;

CREATE TABLE IF NOT EXISTS `questions` (
    `id` INT(11) NOT NULL AUTO_INCREMENT,
    `question_text` VARCHAR(255) NOT NULL,
    `options` LONGTEXT NOT NULL,
    `correct_answer` VARCHAR(255) NOT NULL,
    `difficulty` VARCHAR(40) DEFAULT 'medium',
    `category` VARCHAR(40) DEFAULT 'airport',
    PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;


CREATE TABLE IF NOT EXISTS `scores` (
    `score_id` INT(11) NOT NULL AUTO_INCREMENT,
    `player_name` VARCHAR(40) NOT NULL,
    `score` INT(11) NOT NULL DEFAULT 0,
    `questions_answered` INT(11) DEFAULT 0,
    `correct_answers` INT(11) DEFAULT 0,
    `game_date` TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    PRIMARY KEY (`score_id`),
    INDEX `player_name` (`player_name`),
    INDEX `score` (`score`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;


CREATE TABLE IF NOT EXISTS `airport` (
  `id` INT(11) NOT NULL,
  `ident` VARCHAR(40) NOT NULL,
  `type` VARCHAR(40) DEFAULT NULL,
  `name` VARCHAR(100) DEFAULT NULL,
  `latitude_deg` DOUBLE DEFAULT NULL,
  `longitude_deg` DOUBLE DEFAULT NULL,
  `elevation_ft` INT(11) DEFAULT NULL,
  `continent` VARCHAR(40) DEFAULT NULL,
  `iso_country` VARCHAR(40) DEFAULT NULL,
  `iso_region` VARCHAR(40) DEFAULT NULL,
  `municipality` VARCHAR(100) DEFAULT NULL,
  `scheduled_service` VARCHAR(40) DEFAULT NULL,
  `gps_code` VARCHAR(40) DEFAULT NULL,
  `iata_code` VARCHAR(40) DEFAULT NULL,
  `local_code` VARCHAR(40) DEFAULT NULL,
  `home_link` VARCHAR(100) DEFAULT NULL,
  `wikipedia_link` VARCHAR(100) DEFAULT NULL,
  `keywords` VARCHAR(100) DEFAULT NULL,
  PRIMARY KEY (`ident`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;


CREATE TABLE IF NOT EXISTS `routes` (
    `route_id` INT(11) NOT NULL AUTO_INCREMENT,
    `departure_airport` VARCHAR(40) NOT NULL,
    `arrival_airport` VARCHAR(40) NOT NULL,
    `distance_km` INT(11) DEFAULT NULL,
    `flight_time_minutes` INT(11) DEFAULT NULL,
    `airline` VARCHAR(40) DEFAULT NULL,
    PRIMARY KEY (`route_id`),
    FOREIGN KEY (`departure_airport`) REFERENCES `airport` (`ident`),
    FOREIGN KEY (`arrival_airport`) REFERENCES `airport` (`ident`),
    INDEX `departure_airport` (`departure_airport`),
    INDEX `arrival_airport` (`arrival_airport`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;


